document.addEventListener('DOMContentLoaded',function(){const activeTab=document.querySelector('.tab-link.active');loadPageContent(activeTab.getAttribute('data-target'))});const tabs=document.querySelectorAll('.tab-link');tabs.forEach(tab=>{tab.addEventListener('click',function(){tabs.forEach(t=>t.classList.remove('active'));this.classList.add('active');loadPageContent(this.getAttribute('data-target'))})});function loadPageContent(page){const iframe=document.querySelector('.tab-content iframe');iframe.src=`pages/${page}`}